CREATE DEFINER=`root`@`%` PROCEDURE `proc_batch_simulate_selection`(IN p_class_id INT, IN p_count INT)
BEGIN
    DECLARE i INT DEFAULT 0;
    DECLARE v_stu_id VARCHAR(20);
    DECLARE done INT DEFAULT FALSE;
    
    -- 1. 定义游标：从学生表里找出还没选这门课的前 N 个学生
    DECLARE cur CURSOR FOR 
        SELECT student_id FROM stu_student 
        WHERE student_id NOT IN (SELECT student_id FROM stu_selection WHERE class_id = p_class_id)
        LIMIT p_count;
