CREATE DEFINER=`root`@`%` PROCEDURE `proc_random_kick_students`()
BEGIN
    DECLARE v_class_id INT;
    DECLARE v_excess_count INT;
    DECLARE done INT DEFAULT FALSE;

    DECLARE cur CURSOR FOR 
        SELECT class_id, (selected_num - capacity) 
        FROM stu_class 
        WHERE selected_num > capacity;
        
    DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;

    OPEN cur;
    
    read_loop: LOOP
        FETCH cur INTO v_class_id, v_excess_count;
        IF done THEN
            LEAVE read_loop;
        END IF;

        BEGIN
            DROP TEMPORARY TABLE IF EXISTS tmp_kick_list;
            CREATE TEMPORARY TABLE tmp_kick_list AS
            SELECT selection_id FROM stu_selection 
            WHERE class_id = v_class_id 
            ORDER BY RAND() 
            LIMIT v_excess_count;

            DELETE FROM stu_selection WHERE selection_id IN (SELECT selection_id FROM tmp_kick_list);
            
            -- 如果你有触发器，下面这句通常可以删掉
            UPDATE stu_class SET selected_num = capacity WHERE class_id = v_class_id;
        END;

    END LOOP;

    CLOSE cur;
    
